var prop = require("../properties-parser/index.js");
var sendmail = require('../sendmail')({silent: true})
var objDate = new Date();
var fileName = prop.createEditor("../cucumber-html-reporter/report.txt").get("reportName");
console.log(fileName);
sendmail({
  from: process.argv[3],
  to: process.argv[2],
  cc: process.argv[2],
  subject: 'Test Automation Report - ' + objDate.toLocaleString(),
  html: 'Test Automation Report - ' + objDate.toLocaleString(),
  attachments: [
        {   
            path: '../../../_f5/output/' + fileName,
			contentType: 'text/html;image/png'
        }
  ]
}, function (err, reply) {
  console.log(err && err.stack)
  console.dir(reply)
})
